mapper.select:
SELECT A.SNACK_NO, A.SNACK_NAME, A.PURCHASE_PRICE, A.CATEGORY_NO, C.CATEGORY, A.DETAIL_NO, D.DETAIL, A.RELEASE_PRICE, A.STOCK, B.CHANGE_NAME FROM SNACK A LEFT JOIN SNACK_ATTACHMENT B ON (A.SNACK_NO = B.SNACK_NO) JOIN SNACK_CATEGORY C ON (A.CATEGORY_NO = C.CATEGORY_NO) JOIN SNACK_CATEGORY_DETAIL D ON (A.DETAIL_NO = D.DETAIL_NO) WHERE STATUS = 'Y'
AND A.CATEGORY_NO = #{category}
AND A.DETAIL_NO = #{subCategory}
AND A.SNACK_NAME LIKE '%'||#{search}||'%'
ORDER BY A.CATEGORY_NO ASC, A.DETAIL_NO ASC, A.SNACK_NAME ASC

mapper.update:
INSERT INTO SNACK_D_LIST
SELECT SNACK_D_LIST_SEQ.NEXTVAL, S.*
FROM(

		SELECT #{item.snackListNo}, #{item.snackNo}, #{item.amount}
		FROM SYS.DUAL

)S

mapper.select:
SELECT COUNT(*)
FROM SNACK_D_LIST
WHERE SNACK_LIST_NO = #{snackListNo}
AND SNACK_NO = #{snackNo}

mapper.update:
UPDATE SNACK_D_LIST
SET AMOUNT = ${amount}
WHERE SNACK_D_NO = ${snackDNo}

UPDATE SNACK_LIST
SET TRANS_STATUS = 'Y', TRANS_DATE = SYSDATE, TOTAL_PRICE = #{totalPrice}, BUDGET = #{budget}
WHERE SNACK_LIST_NO = #{listNo}
