mapper.select:
SELECT COUNT (*)
FROM SNACKPOT_EMP
WHERE SEMP_NUM=#{sempNum}

mapper.update:
UPDATE SNACKPOT_EMP
SET SEMP_NAME=#{sempName}, SEMP_DEPT=#{sempDept}, SEMP_JOB=#{sempJob}, SEMP_EMAIL=#{sempEmail}
WHERE SEMP_NUM=#{sempNum}

UPDATE SNACKPOT_EMP
SET SEMP_STATUS='N'
WHERE SEMP_NUM=#{sempNum}

mapper.select:
SELECT SEMP_NAME
FROM SNACKPOT_EMP
WHERE SEMP_NUM=#{sempNum}

mapper.update:
UPDATE SNACKPOT_EMP
SET SEMP_COMCODE=#{comCode}
WHERE SEMP_NUM=#{sempNum}

UPDATE SNACKPOT_EMP
SET SEMP_PW=#{sempPw}
WHERE SEMP_NUM=#{sempNum}

mapper.select:
SELECT *
FROM SNACKPOT_EMP
WHERE SEMP_COMCODE LIKE #{comCode}
